<script>
    function confirmPurchase(packageType, id){
        document.querySelector('.loadingClass').style.display='block';
        window.location.href = '{{url('purchase/confirmation/')}}'+"/"+id+"/"+packageType;
    }

    function closeTel(){
        document.querySelector('.prompt-box').style.display='none';
        document.querySelector('.prompt').style.display='none';
    }

    function openPurchaseModal(modalClass){
        document.querySelector('.'+modalClass).style.display = 'block';
    }

    function closePurchaseModal(modalClass){
        document.querySelector('.'+modalClass).style.display = 'none';
    }


    function tabMenu(_this, tab){
        var tabBox1 = document.querySelector('#tabBox1');
        var tabBox2 = document.querySelector('#tabBox2');
        var tabBox3 = document.querySelector('#tabBox3');

        var tabs = document.querySelectorAll('.item');
        for (let i=0;i<tabs.length;i++){
            if (tabs[i].classList.contains('item_selected')){
                tabs[i].classList.remove('item_selected')
            }
        }
        _this.querySelector('.item').classList.add('item_selected');

        if (tab == 'one'){
            tabBox1.style.display='block';
            tabBox2.style.display='none';
            tabBox3.style.display='none';
        }
        if (tab == 'two'){
            tabBox1.style.display='none';
            tabBox2.style.display='block';
            tabBox3.style.display='none';
        }
        if (tab == 'three'){
            tabBox1.style.display='none';
            tabBox2.style.display='none';
            tabBox3.style.display='block';
        }
    }
</script>
